&lt;p style=&quot;color:#3E3E3E;font-size:16px;background-color:#FFFFFF;&quot;&gt;
	&lt;span style=&quot;line-height:inherit;&quot;&gt;&lt;img src=&quot;/Resources/Upload/weixin/4df5602f-8986-4995-859a-eeeb66f6a4de.gif&quot; style=&quot;width:auto !important;&quot; width=&quot;auto&quot; /&gt;&lt;br /&gt;
