&lt;p&gt;
	&lt;span style=&quot;color:#FF2941;&quot;&gt;&lt;strong&gt;&lt;strong&gt; &lt;/strong&gt;&lt;/strong&gt;&lt;/span&gt; 
&lt;/p&gt;
&lt;p style=&quot;text-align:center;&quot;&gt;
	&lt;strong&gt;&lt;strong&gt; &lt;span style=&quot;color:#FFE500;font-size:18px;background-color:#E53333;&quot;&gt;长按复制添加比智高客服微信号：VVVVVV121216&lt;/span&gt; &lt;/strong&gt;&lt;/strong&gt; 
