&lt;p style=&quot;text-align:center;&quot;&gt;
	&lt;span style=&quot;font-size:15px;line-height:24px;color:#E2561B;&quot;&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/span&gt; 
